import React, { Component } from "react";
import {list} from '../../apis/apiPost'
import {isAuthenticated} from '../../auth'
import defaultProfile from '../../assets/images/useravatar.png'

//import io from "socket.io-client"; 

//import api from "../services/api";

import comment from '../../assets/images/comment.svg';
import like from "../../assets/images/like.svg";
import send from "../../assets/images/send.svg";
import more from "../../assets/images/more.svg";

class Wall extends Component {
  constructor() {
    super();
    this.state = {
      posts: [],
      postIdFor: "",
      like: false,
      likes: 0,
      comments: [],
      hashtags:[],
      place:''
    };

  }

  loadPosts () {
    list().then(data => {
        if (data.error) {
            console.log(data.error);
        } else {
            this.setState({ posts: data });
           console.log(data)
        }
    });
};
//   state = {
//     feed: []
//   };

   async componentDidMount() {
    this.loadPosts();
  }

  renderPosts = posts => {
    const {like, likes, comments, place, hashtags} = this.state
    return (
      <section id="post-list">

          <article key={"posts._id"}>
            <header>

              <div className="user-info">
              <div className="Post-user-avatar">
              {isAuthenticated() && (
                <>
                <img src={isAuthenticated().user._id
                  ? `${process.env.REACT_APP_API_URL}/user/photo/${
                    isAuthenticated().user._id
                    }?${new Date().getTime()}`
                  : defaultProfile} alt={isAuthenticated().user.name}
                  onError={i => (i.target.src = `${defaultProfile}`)}
                  width="23px" height="23px" />
              </>
              )}
            </div>
{posts.map}
                <span>{"post.author"}</span>
                <span className="place">{"post.place"}</span>
              </div>
              <img src={more} alt="More" />
            </header>
            <img
              src="https://www.laravelnigeria.com/img/chris.jpg" alt='user-pf'
            />

            <footer>
              <div className="actions">
                <button >
                  <img src={like} alt="Like" />
                </button>
                <button type="button">
                  <img src={comment} alt="Comment" />
                </button>
                <button type="button">
                  <img src={send} alt="Send" />
                </button>
              </div>
              <strong>{"post.likes"} likes</strong>
              <p>
                {"post.description"}
                <span>{"post.hashtags"}</span>
              </p>
            </footer>
          </article>

      </section>
    )
  }

//   registerToSocket = () => {
//     const socket = io("http://localhost:3333");

//     socket.on("post", newPost => {
//       this.setState({ feed: [newPost, ...this.state.feed] });
//     });

//     socket.on("like", likePost => {
//       this.setState({
//         feed: this.state.feed.map(post =>
//           post._id === likePost._id ? likePost : post
//         )
//       });
//     });
//   };

//   handleLike = id => {
//     api.post(`/posts/${id}/like`);
//   };

  render() {
    return (

      <section id="post-list">

          <article key={"post._id"}>
            <header>

              <div className="user-info">
              <div className="Post-user-avatar">
              <img src="https://www.laravelnigeria.com/img/chris.jpg" alt="Chris" />
            </div>
                <span>{"post.author"}</span>
                <span className="place">{"post.place"}</span>
              </div>
              <img src={more} alt="More" />
            </header>
            <img
              src="https://www.laravelnigeria.com/img/chris.jpg" alt='user-pf'
            />

            <footer>
              <div className="actions">
                <button >
                  <img src={like} alt="Like" />
                </button>
                <button type="button">
                  <img src={comment} alt="Comment" />
                </button>
                <button type="button">
                  <img src={send} alt="Send" />
                </button>
              </div>
              <strong>{"post.likes"} likes</strong>
              <p>
                {"post.description"}
                <span>{"post.hashtags"}</span>
              </p>
            </footer>
          </article>

      </section>

    );
  }
}

export default Wall
