import React, { Component } from "react";
//import api from "../services/api";
import { isAuthenticated } from "../auth";
import { create } from "../apis/apiPost";
import { Redirect } from "react-router-dom";
import LoadingGif from "../assets/images/loading.gif";

import "./New.css";

export default class New extends Component {
  state = {
    photo: "",
    error: "",
    user: {},
    fileSize: 0,
    redirectToProfile: false,
    loading: false,
    place: "",
    description: "",
    hashtags: ""
  };

  componentDidMount() {
    this.setState({ user: isAuthenticated().user });
    this.postData = new FormData();
  }

  isValid = () => {
    const { description, place, hashtags, fileSize } = this.state;
    if (fileSize > 51200) {
      this.setState({ error: "File should be less than 5120kb or 5mb" });
      return false;
    }
    if (description.length === 0 || hashtags.length === 0) {
      this.setState({ error: "Description and hashtags are required", loading: false });
      return false;
    }
    return true;
  };

  handleChange = name => event => {
    this.setState({ error: "" });
    const value = name === "photo" ? event.target.files[0] : event.target.value;
    this.postData.set(name, value);
    const fileSize = name === "photo" ? event.target.files[0].size : 0;
    this.setState({ [name]: value, fileSize });
  };

  handleImageChange = e => {
    this.setState({ image: e.target.files[0] });
  };

  clickSubmit = e => {
    e.preventDefault();
    this.setState({ loading: true });
    if (this.isValid()) {
      const userId = isAuthenticated().user._id;
      const token = isAuthenticated().token;

      create(userId, token, this.postData).then(data => {
        if (data.error) this.setState({ error: data.error });
        else {
          this.setState({
            loading: false,
            title: "",
            body: "",
            photo: "",
            redirectToProfile: true
          });
        }
        //
      });
    }
  };

  // handleSubmit = async e => {
  //   e.preventDefault();
  //   const data = new FormData();

  //   data.append("image", this.state.image);
  //   data.append("author", this.state.author);
  //   data.append("place", this.state.place);
  //   data.append("description", this.state.description);
  //   data.append("hashtags", this.state.hashtags);

  //  // await api.post("posts", data);

  //   this.props.history.push("/");
  // };

  render() {
    return (
      <div className='container'>

      <form className='mt-5'>
      <div className="form-group"><input type="file" name="image" onChange={this.handleImageChange} /></div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Description</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="description"
          placeholder="Post's Description"
          onChange={this.handleChange}
          value={this.state.description}
        />
      </div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Location</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="place"
          placeholder="Post's place"
          onChange={this.handleChange}
          value={this.state.place}
        />
      </div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Hashtags</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="hashtags"
          placeholder="Post's Hashtags"
          onChange={this.handleChange}
          value={this.state.hashtags}
        />
      </div>
      <button onSubmit={this.handleSubmit} className="btn btn-raised btn-info btn-lg waves-effect waves-light btn-block">
        Post
      </button>
    </form>
    </div>
    );
  }
}
