import React, { Component } from 'react'
import {isAuthenticated} from '../../auth'
import { Redirect, Link } from "react-router-dom";
import { read} from '../../apis/apiUser'
import defaultProfile from '../../assets/images/useravatar.png'
import loadingSkeleton from '../../assets/images/skeleton-loading.gif'
import {listByUser} from '../../apis/apiPost'
import './profile.css'
import Cog from '../../assets/images/cog-solid.svg'

class ProfileBody extends Component {
  constructor() {
    super();
    this.state = {
      user: { following: [], followers: [] },
      redirectToSignIn: false,
      following: false,
      error: "",
      posts: [],
      loading: false
    };
  }

  checkFollow = user => {
    const jwt = isAuthenticated();
    const match = user.followers.find(follower => {
      return follower._id === jwt.user._id;
    });
    return match;
  };

  clickFollowButton = callApi => {
    const userId = isAuthenticated().user._id;
    const token = isAuthenticated().token;

    callApi(userId, token, this.state.user._id).then(data => {
      if (data.error) {
        this.setState({ error: data.error });
      } else {
        this.setState({ user: data, following: !this.state.following });
      }
    });
  };

  init = userId => {
    this.setState({loading: true})
    const token = isAuthenticated().token;
    read(userId, token).then(data => {
      if (data.error) {
        this.setState({ redirectToSignIn: true });
      } else {
        let following = this.checkFollow(data);
        this.setState({ user: data, following, loading: false });
        this.loadPosts(data._id);
      }
    });
  };

  loadPosts = userId => {
    const token = isAuthenticated().token;
    listByUser(userId, token).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        console.log(data)
        this.setState({ posts: data });
      }
    });
  };

  componentDidMount() {
    const userId = this.props.match.params.userId;    
    this.init(userId);
  }

  componentWillReceiveProps(props) {
    const userId = props.match.params.userId;
    this.init(userId);
  }

  render() {
    const { redirectToSignIn, user, posts, loading } = this.state;
    if (redirectToSignIn) return <Redirect to="/sign-in" />;

    const photoUrl = user._id
      ? `${process.env.REACT_APP_API_URL}/user/photo/${
          user._id
        }?${new Date().getTime()}`
      : defaultProfile;
    return (
      <div className='container'>
      {loading ? (
        <img className='img-fluid' width='auto'  src={LoadingSkeleton} alt='loading'/>
    ) : (
      <>
      <header>

      <div className="container">

        <div className="profile">

          <div className="profile-image">

            <img src={photoUrl}
            onError={i => (i.target.src = `${DefaultProfile}`)}
            alt={user.name}
            height="150"
            width="150"
            style={{ borderRadius: "50%" }}/>

          </div>

          <div className="profile-user-settings">

            <h1 className="profile-user-name">{user.name}_</h1>

            <button className="btn profile-edit-btn">Edit Profile</button>

            <button className="btn profile-settings-btn" aria-label="profile settings"><img  src={Cog} alt='setting'/></button>

          </div>

          <div className="profile-stats">

            <ul>
              <li><span className="profile-stat-count">164</span> posts</li>
              <li><span className="profile-stat-count">188</span> followers</li>
              <li><span className="profile-stat-count">206</span> following</li>
            </ul>

          </div>

          <div className="profile-bio">

            <p><span className="profile-real-name">Jane Doe</span> Lorem ipsum dolor sit, amet consectetur adipisicing elit </p>

          </div>

        </div>


      </div>



    </header>

  <main>

      <div className="container">

        <div className="gallery">

          <div className="gallery-item" tabindex="0">

            <img src="https://images.unsplash.com/photo-1511765224389-37f0e77cf0eb?w=500&h=500&fit=crop" className="gallery-image" alt=""/>

            <div className="gallery-item-info">

              <ul>
                <li className="gallery-item-likes"><span className="visually-hidden">Likes:</span><i className="fas fa-heart" aria-hidden="true"></i> 56</li>
                <li className="gallery-item-comments"><span className="visually-hidden">Comments:</span><i className="fas fa-comment" aria-hidden="true"></i> 2</li>
              </ul>

            </div>

