import React, { Component } from 'react'
import { Redirect } from 'react-router-dom';
//import { signin, authenticate } from '../auth'
import './signIn-signUp.css'
import './ss'

class SignIn extends Component {constructor() {
    super();
    this.state = {

      email: "",
      password: "",
      error: "",
      redirectToReferer: false,
      loading: false
    };
  }

  handleChange = name => event => {
    this.setState({ error: "" });
    this.setState({ [name]: event.target.value });
  };

  clickSubmit = e => {
    e.preventDefault();
    this.setState({loading: true})

    const {email, password } = this.state;

    const user = { email, password };

    // signin(user).then(data => {
    //   if (data.error) {
    //     this.setState({ error: data.error, loading: false });   
    //   } else {
    //       //auth user
    //     authenticate(data, () => {
    //         this.setState({redirectToReferer: true})
    //     })
    //       //redirect
    //   }

    // });
  };

  signinForm = ( email, password) => (
    <form>

      <div className="form-group">
        <label className=" bold">Email</label>
        <input
          onChange={this.handleChange("email")}
          type="email"
          className="form-control text-dark bold"
          value={email}
        />
      </div>
      <div className="form-group">
        <label className=" bold">Password</label>
        <input
          onChange={this.handleChange("password")}
          type="password"
          className="form-control text-dark bold"
          value={password}
        />
      </div>
      <button onClick={this.clickSubmit} className="btn btn-raised btn-primary waves-effect waves-light btn-block">
        Sign-In
      </button>
    </form>
  );

  render() {
    const { email, password, error, redirectToReferer, loading  } = this.state;

    if(redirectToReferer) {
        return <Redirect to='/'/>
    }
    return (
      <div className="container">
        <h2 className="mt-5 mb-5">Signin</h2>

        <div
          className="alert alert-danger"
          style={{ display: error ? "" : "none" }}
        >
          {error}
        </div>

        {loading ? <div className="jumbotron text-center"><h2>Loading...</h2></div> : ""}

        {this.signinForm( email, password)}


        <div class="col-md-6 mb-4">
        <div class="card">
            <div class="card-body">
                <h3 class="text-center default-text py-3"><i class="fa fa-lock"></i> Login:</h3>

                <div class="md-form">
                    <i class="fa fa-envelope prefix grey-text"></i>
                    <input type="text" id="defaultForm-email" class="form-control"/>
                    <label for="defaultForm-email">Your email</label>
                </div>
                <div class="md-form">
                    <i class="fa fa-lock prefix grey-text"></i>
                    <input type="password" id="defaultForm-pass" class="form-control"/>
                    <label for="defaultForm-pass">Your password</label>
                </div>
                <div class="text-center">
                    <button class="btn btn-default waves-effect waves-light">next</button>
                </div>
            </div>
        </div>
    </div>

      </div>
    );
  }
}

export default SignIn;
