import React, { Component } from "react";
//import api from "../services/api";
import { isAuthenticated } from "../auth";
import { create } from "../apis/apiPost";
import { Redirect } from "react-router-dom";
import LoadingGif from "../assets/images/";

import "./New.css";

export default class New extends Component {
  state = {
    image: null,
    author: "",
    place: "",
    description: "",
    hashtags: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleImageChange = e => {
    this.setState({ image: e.target.files[0] });
  };

  handleSubmit = async e => {
    e.preventDefault();
    const data = new FormData();

    data.append("image", this.state.image);
    data.append("author", this.state.author);
    data.append("place", this.state.place);
    data.append("description", this.state.description);
    data.append("hashtags", this.state.hashtags);

   // await api.post("posts", data);

    this.props.history.push("/");
  };

  render() {
    return (
      <div className='container'>

      <form className='mt-5'>
      <div className="form-group"><input type="file" name="image" onChange={this.handleImageChange} /></div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Description</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="description"
          placeholder="Post's Description"
          onChange={this.handleChange}
          value={this.state.description}
        />
      </div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Location</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="place"
          placeholder="Post's place"
          onChange={this.handleChange}
          value={this.state.place}
        />
      </div>
      <div className="form-group">
      <i class="fa fa-lock prefix grey-text"></i>
        <label className=" bold">Hashtags</label>
        <input
          className="form-control text-dark bold"
          type="text"
          name="hashtags"
          placeholder="Post's Hashtags"
          onChange={this.handleChange}
          value={this.state.hashtags}
        />
      </div>
      <button onSubmit={this.handleSubmit} className="btn btn-raised btn-info btn-lg waves-effect waves-light btn-block">
        Post
      </button>
    </form>
    </div>
    );
  }
}
